import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Header from "../components/layout/header";
import Footer from "../components/layout/footer";
import "../styles/jobrequests.css";

const JobRequests = () => {
  const [requests, setRequests] = useState([]);
  const [userLocation, setUserLocation] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate(); // useNavigate hook for navigating to job details page

  // Sample job requests data (you can replace this with real API calls)
  const mockRequests = [
    { id: 1, name: "Plumbing Job", job: "Plumbing", description: "Fix a leaking pipe", location: "Location 1" },
    { id: 2, name: "Electrical Job", job: "Electrical", description: "Install new lighting", location: "Location 2" }
  ];

  useEffect(() => {
    if (!navigator.geolocation) {
      alert("Geolocation is not supported by your browser.");
      setLoading(false);
      return;
    }

    navigator.geolocation.getCurrentPosition(
      (position) => {
        const { latitude, longitude } = position.coords;
        setUserLocation({ latitude, longitude });
      },
      (error) => {
        alert("Failed to get location. Please enable location services.");
        setLoading(false);
      }
    );
  }, []);

  useEffect(() => {
    if (userLocation) {
      // Filtering mock requests based on location (use real data and filtering logic for production)
      const filteredRequests = mockRequests.filter((req) => {
        return (
          Math.random() < 0.5 // Mock condition: Just randomly showing jobs for demo
        );
      });
      setRequests(filteredRequests); // Set the filtered requests from the mock data
      setLoading(false);
    }
  }, [userLocation]);

  const handleDecision = (id, action) => {
    if (action === "accept") {
      const acceptedJob = requests.find((req) => req.id === id);
      localStorage.setItem("acceptedJob", JSON.stringify(acceptedJob)); // Store the accepted job in localStorage
      navigate("/workerdashboard"); // Redirect to the worker dashboard
    } else {
      setRequests((prev) => prev.filter((req) => req.id !== id)); // Reject the job and remove it from the list
    }
  };

  return (
    <>
      <Header />
      <div className="job-requests-page">
        <h1>Job Requests</h1>
        {loading ? (
          <p>Loading job requests...</p>
        ) : requests.length === 0 ? (
          <p>No job requests found within your location.</p>
        ) : (
          requests.map((req) => (
            <div className="job-request-card" key={req.id}>
              <h2>{req.name}</h2>
              <p>{req.job}</p>
              <p>{req.description}</p>
              <div className="job-request-actions">
                <button onClick={() => handleDecision(req.id, "accept")}>Accept</button>
                <button onClick={() => handleDecision(req.id, "reject")}>Reject</button>
                <button onClick={() => navigate(`/job-details/${req.id}`)}>View Details</button>
              </div>
            </div>
          ))
        )}
      </div>
      <Footer />
    </>
  );
};

export default JobRequests;
